import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { withRouter } from "react-router-dom";
import * as localActions from "./actions";
import DatePicker from "react-datepicker";
import { Header } from "../common";
import "./home.scss";
import "react-datepicker/dist/react-datepicker.css";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate: new Date().getTime(),
      toDate: new Date().getTime(),
      github:''
    };
    this.handleFromChange = this.handleFromChange.bind(this);
    this.handleToChange = this.handleToChange.bind(this);
  }

  componentDidMount() {
    this.props.listHotels();

  }
  handleFromChange(date) {
    this.setState({
      startDate: new Date(date).getTime()
    },()=> this.props.StartDateToStor(this.state.startDate));
  }
  handleToChange(date) {


    this.setState(
      {
        toDate: new Date(date).getTime()
      },()=> this.props.endDateToStor(this.state.startDate));

  }
  filterDates = () => {
    this.props.filterHotels(
      this.state.startDate,
      this.state.toDate
    );
  };

  render() {
    return (
      <div>
        <Header header="Search by Hotels" />
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { home } = state;
  return { home };
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ ...localActions }, dispatch);
}

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Home)
);
